                  c_strDatafileID,
                  c_strNumber,
                  NULL,
                  NULL,
                  --indx, 
                  v_intCount,
                  c_strDatafileID,
                  NULL); -- Standardization group
        v_intCount := v_intCount + 1;
        --      indx       := indx + 1;
        -- Insert LineNo Record
        InsertRec(p_recDatafile.Filetype_Id,
                  cc_fieldtype.GetFieldTypeID(p1 => c_strET),
                  c_strLineNo,
                  c_strRecnum,
