  FUNCTION GetPassedQCFiles(p_status         IN datafile.file_status%TYPE,
                            p_intCustomer_ID IN customers.customer_id%TYPE)
    RETURN t_Files IS
    v_strFailFIle VARCHAR2(40) := 'FAIL FILE';
    --cursor for ETPASSQC
    -- must include files with IGNORE action status
    CURSOR c1 IS
      SELECT *
        FROM DATAFILE d
       WHERE datafile_id IN
             (SELECT UNIQUE d.datafile_id
                FROM datafile           d,
                     qc_result_datafile qrd,
                     qc_result          qr,
                     qc_test            qc
               WHERE d.file_status = p_status
                 AND d.datafile_id = qrd.datafile_id
                 AND qrd.qc_result_id = qr.qc_result_id
                 AND qr.qc_test_id = qc.qc_test_id
                 AND qc.test_category = 'ET'
                 AND (qr.qc_test_count = 0 OR
                     qc.qc_action_id IN
                     (SELECT qc_action_id
                         FROM qc_action
                        WHERE qc_action_name != v_strFailFile)))
         AND d.filetype_id IN
             (SELECT filetype_id
                FROM filetype
               WHERE site_id IN
                     (SELECT site_id
                        FROM site
                       WHERE customer_id = p_intCustomer_ID));
    -- cursor for ITPASSQC
    CURSOR c2 IS
      SELECT *
        FROM DATAFILE d
       WHERE datafile_id IN
             (SELECT UNIQUE d.datafile_id
                FROM datafile           d,
                     qc_result_datafile qrd,
                     qc_result          qr,
                     qc_test            qc
               WHERE d.file_status = p_status
                 AND d.datafile_id = qrd.datafile_id
                 AND qrd.qc_result_id = qr.qc_result_id
                 AND qr.qc_test_id = qc.qc_test_id
                 AND qc.test_category = 'IT'
                 AND (qr.qc_test_count = 0 OR
                     qc.qc_action_id IN
                     (SELECT qc_action_id
                         FROM qc_action
                        WHERE qc_action_name != v_strFailFile)))
         AND d.filetype_id IN
             (SELECT filetype_id
                FROM filetype
               WHERE site_id IN
                     (SELECT site_id
                        FROM site
                       WHERE customer_id = p_intCustomer_ID));
    -- cursor for STAGEPASSQC
    CURSOR c3 IS
      SELECT *
        FROM DATAFILE d
       WHERE datafile_id IN
             (SELECT UNIQUE d.datafile_id
                FROM datafile           d,
                     qc_result_datafile qrd,
                     qc_result          qr,
                     qc_test            qc
               WHERE d.file_status = p_status
                 AND d.datafile_id = qrd.datafile_id
                 AND qrd.qc_result_id = qr.qc_result_id
                 AND qr.qc_test_id = qc.qc_test_id
                 AND qc.test_category = 'STAGE'
                 AND (qr.qc_test_count = 0 OR
                     qc.qc_action_id IN
                     (SELECT qc_action_id
                         FROM qc_action
                        WHERE qc_action_name != v_strFailFile)))
         AND d.filetype_id IN
             (SELECT filetype_id
                FROM filetype
               WHERE site_id IN
                     (SELECT site_id
                        FROM site
                       WHERE customer_id = p_intCustomer_ID));
    v_Files t_Files;
  BEGIN
    CASE p_status
      WHEN c_strETQC THEN
        OPEN c1;
        FETCH c1 BULK COLLECT
          INTO v_Files;
        CLOSE c1;
      WHEN c_strITQC THEN
        OPEN c2;
        FETCH c2 BULK COLLECT
          INTO v_Files;
        CLOSE c2;
      WHEN c_strSTAGEQC THEN
        OPEN c3;
        FETCH c3 BULK COLLECT
          INTO v_Files;
        CLOSE c3;
    END CASE;
    RETURN v_Files;
  EXCEPTION
    WHEN OTHERS THEN
      PLF_EH.logerror;
      RAISE;
  END GetPassedQCFiles;
