    PROCEDURE CleanUpET(p_intDataFileID IN datafile.datafile_id%TYPE) IS
        v_recFile        datafile%ROWTYPE;
        v_recState       system_state%ROWTYPE;
        v_recStagePassQC system_state%ROWTYPE;
    BEGIN
