CREATE PACKAGE BODY         CC_EXPORT IS
  -- Global variable declarations
  --    gv_intExportNumber INTEGER;
  --    gv_strFileName     VARCHAR2(255);
  --    gv_dateStartTime   DATE;
  --    gv_dateEndTime     DATE;
