	) IS
	BEGIN
		INSERT INTO filefield
			(filetype_id,
			 fieldtype_id,
			 mf_id,
			 field_name,
			 field_datatype,
			 field_length,
			 field_precision,
			 field_order,
			 field_begin,
			 field_end,
			 field_desc,
			 field_method,
			 nullable,
			 standardization_group)
		VALUES
			(p_intFiletypeID,
			 p_intFieldtypeID,
			 p_intMFID,
			 p_strField_name,
			 p_strField_datatype,
			 p_intField_length,
			 p_intField_precision,
			 p_intField_order,
			 p_intField_begin,
			 p_intField_end,
			 p_strField_desc,
			 p_strField_method,
			 p_strNullable,
