    FUNCTION RunQuery(p_strSQL IN qc_test.sql_text%TYPE)
      RETURN cc_qc_failed_record.t_Line_No IS
      c1       SYS_REFCURSOR;
      v_result cc_qc_failed_record.t_Line_No;
