            SELECT *
              FROM system_state
             WHERE state = p_strState;
        v_recState system_state%ROWTYPE;
    BEGIN
        OPEN c1;
        FETCH c1
            INTO v_recState;
        CLOSE c1;
        RETURN v_recState;
    EXCEPTION
        WHEN OTHERS THEN
            PLF_EH.logerror;
            RAISE;
