        SELECT *
          FROM system_state t
         WHERE state_order IN (SELECT UNIQUE(state_order)
                                 FROM datafile d, system_state s
                                WHERE d.file_status = s.state)
         ORDER BY state_order ASC;
      v_states t_states;
    BEGIN
      OPEN c1;
      FETCH c1 BULK COLLECT
        INTO v_states;
      CLOSE c1;
      RETURN v_states;
    EXCEPTION
      WHEN OTHERS THEN
        PLF_EH.logerror;
        RAISE;
    END GetStatesFromFiles;
BEGIN
