  PROCEDURE provisionCompany(companyName     IN customers.customer_name%TYPE,
                             RedArilClientId IN VARCHAR2,
                             companyId       OUT customers.customer_id%TYPE,
                             default_dashboard IN dashboard_users.default_dashboard%TYPE) IS
