 	PROCEDURE create_alert(p_intCustomerID       IN customers.customer_id%TYPE,
 								  p_intCreator          IN dashboard_users.dashboard_user_id%TYPE,
 								  p_intOwner            IN dashboard_users.dashboard_user_id%TYPE,
