FUNCTION get_active_alerts(p_intRegID IN alert_tables.dcn_regid%TYPE)
    RETURN t_alerts IS
    v_tabAlerts t_alerts;
    v_tablen    INTEGER;
    v_tablelist VARCHAR2(4000);

BEGIN
    --This is new logic. JBS 3/4/08  
    SELECT v.alert_id,
           v.customer_id,
           v.creator,
           v.owner,
           v.alert_name,
           v.start_time,
           v.end_time,
           v.num_alerts,
           v.num_tests,
           v.times_tested,
           v.max_frequency,
           v.max_frequency_unit,
           v.status,
           v.alert_description,
           v.message_template,
           v.test_sql BULK COLLECT
      INTO v_tabAlerts
      FROM (SELECT l.delivery_time,
                   l.dt,
                   a.alert_id,
                   a.customer_id,
                   a.creator,
                   a.owner,
                   a.alert_name,
                   a.start_time,
                   a.end_time,
                   a.num_alerts,
                   a.num_tests,
                   a.times_tested,
                   a.max_frequency,
                   a.max_frequency_unit,
                   a.status,
                   a.alert_description,
                   a.message_template,
                   a.test_sql
              FROM alerts a,
                   alert_tables at,
                   (SELECT t.alert_id,
                           MAX(delivery_time) delivery_time,
                           to_char(delivery_time, 'YYYYMMDD HH24:MI') dt
                      FROM alert_delivery_log t
                     GROUP BY t.alert_id,
                              to_char(delivery_time, 'YYYYMMDD HH24:MI')) l
             WHERE a.alert_id = at.alert_id
               AND a.alert_id = l.alert_id(+)
               AND a.start_time < SYSDATE
               AND a.end_Time > SYSDATE
               AND a.status = 'ACTIVE'
               AND at.dcn_regid = p_intRegID
             GROUP BY a.alert_Id,
                      a.customer_id,
                      a.creator,
                      a.owner,
                      a.alert_name,
                      a.start_time,
                      a.end_time,
                      a.num_alerts,
                      a.num_tests,
                      a.times_tested,
                      a.max_frequency,
                      a.max_frequency_unit,
                      a.status,
                      a.alert_description,
                      a.message_template,
                      a.test_sql,
                      l.dt,
                      l.delivery_time,
                      at.dcn_regid) v
     GROUP BY v.alert_id,
              v.customer_id,
              v.creator,
              v.owner,
              v.alert_name,
              v.start_time,
              v.end_time,
              v.num_alerts,
              v.num_tests,
              v.times_tested,
              v.max_frequency,
              v.max_frequency_unit,
              v.status,
              v.alert_description,
              v.message_template,
              v.test_sql
    HAVING COUNT(*) < v.num_alerts 
    AND SYSDATE > ((NVL(MAX(v.delivery_time), SYSDATE - 9999)) + (v.max_frequency * v.max_frequency_unit) / 60 / 24);

    RETURN v_tabAlerts;

END get_active_alerts;


      PROCEDURE create_alert(p_intCustomerID      IN CUSTOMERS.CUSTOMER_ID%TYPE,
