       AND d.datafile_id = p_intDataFileID
       and u.username like UPPER('%' || sv_comlib.build_system_name(c.customer_name) ||c_STAGE);
       
    RETURN v_stageschema;
  END;

BEGIN
  NULL;
END sv_comlib;
/

