  												p_strDefaultTablespace IN CUSTOMER_SCHEMAS.DEFAULT_TABLESPACE%TYPE,
  												p_strSchemaPassword    IN CUSTOMER_SCHEMAS.SCHEMA_PASSWORD%TYPE,
