  	BEGIN
  		DELETE FROM CUSTOMER_SCHEMAS
  		 WHERE CUSTOMER_SCHEMA_ID = p_intCustomerSchemaId;
	
  	END delete_customer_schema;


  BEGIN
  	NULL;
  END sv_customer_schemas_dml;
 
 
