	PROCEDURE delete_dashboard(p_intDashboardId IN sv_DASHBOARDS.sv_DASHBOARD_ID%TYPE) IS
	BEGIN
		DELETE FROM sv_DASHBOARDS
		 WHERE sv_DASHBOARD_ID = p_intDashboardId;
	
	END delete_dashboard;

BEGIN
	NULL;
END sv_dashboards_dml;
