													FROM (SELECT a.*,
																	 rownum rn
															  FROM (SELECT password_history_id
																		 FROM password_history
																		WHERE dashboard_user_id = p_intDashboardUserId
																		ORDER BY change_date DESC) a)
												  WHERE rn > p_intMinHistoryBuffer);
	END trim_user_history;

