	PROCEDURE create_password_policy
	(
		p_intCustomerId              IN PASSWORD_POLICIES.CUSTOMER_ID%TYPE,
		p_strPolicyName              IN PASSWORD_POLICIES.POLICY_NAME%TYPE,
		p_intPasswordLength          IN PASSWORD_POLICIES.PASSWORD_LENGTH%TYPE,
