			 FAILED_LOGIN_LOCK_UNIT,
			 INACTIVE_GRACE_PERIOD,
			 INACTIVE_GRACE_UNIT,
			 PASSWORD_HISTORY_BUFFER)
		VALUES
			(PASSWORD_POLICIES_SEQ.NEXTVAL,
			 p_strPolicyName,
			 p_intCustomerId,
			 p_intPasswordLength,
			 p_intPasswordDuration,
			 p_strRequireNumeric,
			 p_intNumericChars,
			 p_strRequireUppercase,
