				clientHost = 'svprodapp3.sa' OR
				clientHost = 'lg-ap.sharpanalytics.com' OR
        clientHost = 'lg-cdn.sharpanalytics.com'
		THEN
			envName := 'PROD';
		ELSE
			envName := 'DEV';
		END IF;
	
		envRecord := sv_database_environments_dml.get_database_environment(p_strEnvironmentName => envName);
